mapper.select:
select t_clazz.* from t_clazz
where
(c_no like '%${keyword}%' or c_name like '%${keyword}%'") order by ${orderBy} ${orderWay} limit ${offset},${pageSize}

select COUNT(cNo) from t_clazz

	where

c_no=#{cNo}

mapper.update:
update t_clazz
set t_clazz.c_no=#{cNo}
t_clazz.c_name=#{cName}
t_clazz.c_enter_year=#{cEnterYear}
where c_no=#{cNo}

mapper.select:
select c_no,c_name,c_enter_year from t_clazz left join t_student on t_student.s_clazz_fk=t_clazz.id left join t_depart
on s_student.s_depart_fk=t_depart.id left join t_teacher on t_teacher.t_depart_fk=depart.id
where t_no=#{tNo}

select COUNT(s_no) from t_clazz left join t_student on t_student.s_clazz_fk=t_clazz.id

	where

c_no=#{param.cNo}
