mapper.select:
select t_course.* from t_course
where
(co_no like '%${keyword}%' or co_name like '%${keyword}%'") order by ${orderBy} ${orderWay} limit ${offset},${pageSize}

select COUNT(coNo) from t_course

	where

co_no=#{coNo}

mapper.update:
update t_course
set t_course.co_no=#{coNo}
t_course.co_name=#{coName}
where cn_no=#{coNo}

mapper.select:
select s_no,s_name,s_sex,s_birthday,s_enter_year from t_course left join t_teacher on t_teacher.t_course_fk=t_course.id left join t_depart on t_teacher.t_depart_fk=t_depart.id
left join t_student on t_student.t_depart_fk=t_depart.id
where co_no=#{coNo}

select COUNT(s_no) from t_course left join t_teacher on t_teacher.t_course_fk=t_course.id left join t_depart on t_teacher.t_depart_fk=t_depart.id left join t_student on t_student.t_depart_fk=t_depart.id
where
co_no=#{coNo}
