mapper.select:
select t_depart.* from t_depart
where
d_no like '%${param.keyword}%' or d_name like '%${param.keyword}%' order by ${param.orderBy} ${param.orderWay} limit ${param.offset},${param.pageSize}

select COUNT(dNo) from t_depart

	where

d_no=#{param.dNo}

mapper.update:
update t_depart
set t_depart.d_name=#{param.dName}
where d_no=#{dNo}

mapper.select:
select COUNT(s_no) from t_depart left join t_student on t_student.s_depart_fk=t_depart.id

	where

d_no=#{param.dNo}

select COUNT(co_no) from t_depart left join t_teacher on t_teacher.t_depart_fk=t_depart.id left join t_course on t_student.t_course_fk=t_course.id

	where

d_no=#{param.dNo}

select COUNT(t_no) from t_depart left join t_teacher on t_teacher.t_depart_fk=t_depart.id		

	where

d_no=#{param.dNo}
