mapper.select:
select COUNT(s_no) from t_student

	where

s_no=#{sNo}

select co_no,co_name from t_student left join t_depart on t_student.s_depart_fk=t_depart.d_no left join t_teacher on t_teacher.t_depart_fk=t_depart.d_no
left join t_course on t_teacher.t_course_fk=t_course.co_no
where s_no=#{sNo}
