mapper.select:
select t_teacher.*,d_no,d_name,co_no,co_name,r_property,r_name from t_teacher left join t_depart on t_teacher.t_depart_fk=t_depart.d_no
left join t_course on t_teacher.t_course_fk=t_course.co_no left join t_role on t_teacher.t_role_fk=t_role.id

	where

t_depart_fk=${departcNo}
(t_no like '%${param.keyword}%' or t_name like '%${param.keyword}')
order by ${param.orderBy} ${param.orderWay} limit ${param.offset},${param.pageSize}

select COUNT(param.tNo) from t_teacher		

	where

t_no=#{param.tNo}

mapper.update:
update t_teacher
set t_teacher.t_name=#{param.tName},
t_teacher.t_sex=#{param.tSex}
where t_no=#{tNo}
