mapper.update:
update t_user
set t_user.u_userpass=#{password}
where t_user.u_username=#{username}

mapper.select:
select t_user.*,r_property,r_name from t_user left join t_role on t_user.t_role_fk=t_role.id

	where

t_role_fk=${roleId}
(t_user.id like '%${param.keyword}%' or u_username like '%${param.keyword}')
order by ${param.orderBy} ${param.orderWay} limit ${param.offset},${param.pageSize}

select i_name,i_sex,i_photo,i_address,i_brithday,i_idcard from 
left join t_user_info on t_user.u_username=t_user_info.i_username_fk
where t_user.u_username=#{username}

select r_name from 
left join t_role on t_user.u_role_fk=t_role.id
where t_user.u_username=#{username}

select COUNT(param.Id) from t_user

	where

t_user.id=#{param.Id}

select u_username,u_phone from t_user where
u_phone=#{phonenum} and
u_username=#{username}
